mapper.select:
SELECT a.*,b.name as material_name,c.name as workshop_name ,d.name as  stocktype_name
FROM stock a 
left join material b on a.material=b.id 
left join workshop c on a.workshop=c.id 
left join stocktype d on a.stocktype=d.id 


	 AND a.ID =#{id} 
	 AND a.MATERIAL =#{material} 
	 AND a.STOCKTYPE =#{stocktype} 
	 AND a.WORKSHOP =#{workshop} 
	 AND a.UNIT =#{unit} 
	 AND a.MSIZE =#{msize} 
	 AND cast(a.HEIGHT as DECIMAL) >=#{heightFrom} 
	 AND cast(a.HEIGHT as DECIMAL) <=#{heightTo} 
	 AND a.HEIGHT =#{height} 
	 AND cast(a.NUMBER as DECIMAL) >=#{numberFrom} 
	 AND cast(a.NUMBER as DECIMAL) <=#{numberTo} 
	 AND a.NUMBER =#{number} 
	 AND a.COMMENT =#{comment} 
	 AND a.QUALIFY =#{qualify} 
	 AND a.DAMAGE =#{damage} 
	 AND a.STATE =#{state} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 

	 AND a.PROCESS =#{process} 
	 AND a.PACKAGING =#{packaging} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.ORDERCODE =#{ordercode} 
	 AND a.OUTTYPE =#{outtype} 
	 AND a.MAOCHI =#{maochi} 
	 AND a.JINGCHI =#{jingchi} 
	 AND a.YANMI =#{yanmi} 
	 AND a.CODE =#{code} 
	 AND unix_timestamp(a.M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(a.M_DT) <=unix_timestamp(#{m_dtTo}) 
	 AND a.M_DT =#{m_dt} 
	 AND a.AUDITOR =#{auditor} 
	 AND a.WORKER =#{worker} 
	 AND cast(a.SPRICE as DECIMAL) >=#{spriceFrom} 
	 AND cast(a.SPRICE as DECIMAL) <=#{spriceTo} 
	 AND a.SPRICE =#{sprice} 
	 AND cast(a.SSUM as DECIMAL) >=#{ssumFrom} 
	 AND cast(a.SSUM as DECIMAL) <=#{ssumTo} 
	 AND a.SSUM =#{ssum} 


ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM stock 

	 AND ID =#{id} 
	 AND MATERIAL =#{material} 
	 AND STOCKTYPE =#{stocktype} 
	 AND WORKSHOP =#{workshop} 
	 AND UNIT =#{unit} 
	 AND MSIZE =#{msize} 
	 AND HEIGHT =#{height} 
	 AND NUMBER =#{number} 
	 AND COMMENT =#{comment} 
	 AND QUALIFY =#{qualify} 
	 AND DAMAGE =#{damage} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND C_ID =#{c_id} 
	 AND PID =#{pid} 

	 AND PROCESS =#{process} 
	 AND PACKAGING =#{packaging} 
	 AND WORKGROUP =#{workgroup} 
	 AND ORDERCODE =#{ordercode} 
	 AND OUTTYPE =#{outtype} 
	 AND MAOCHI =#{maochi} 
	 AND JINGCHI =#{jingchi} 
	 AND YANMI =#{yanmi} 
	 AND CODE =#{code} 
	 AND M_DT =#{m_dt} 
	 AND AUDITOR =#{auditor} 
	 AND WORKER =#{worker} 
	 AND a.SPRICE =#{sprice} 
	 AND a.SSUM =#{ssum}

SELECT a.*,b.name as material_name,c.name as workshop_name,
SUM(CASE WHEN a.state=0 THEN a.number WHEN a.state=1 THEN -a.number ELSE 0 END) as sum_number 
FROM stock a 
left join material b on a.material=b.id 
left join workshop c on a.workshop=c.id 


	 AND a.ID =#{id} 
	 AND a.MATERIAL =#{material} 
	 AND a.STOCKTYPE =#{stocktype} 
	 AND a.WORKSHOP =#{workshop} 
	 AND a.UNIT =#{unit} 
	 AND a.MSIZE =#{msize} 
	 AND a.HEIGHT =#{height} 
	 AND a.NUMBER =#{number} 
	 AND a.COMMENT =#{comment} 
	 AND a.QUALIFY =#{qualify} 
	 AND a.DAMAGE =#{damage} 
	 AND a.STATE =#{state} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.PROCESS =#{process} 
	 AND a.PACKAGING =#{packaging} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.ORDERCODE =#{ordercode} 
	 AND a.OUTTYPE =#{outtype} 
	 AND a.MAOCHI =#{maochi} 
	 AND a.JINGCHI =#{jingchi} 
	 AND a.YANMI =#{yanmi} 
	 AND a.CODE =#{code} 
	 AND a.M_DT =#{m_dt} 
	 AND a.AUDITOR =#{auditor} 
	 AND a.WORKER =#{worker} 

GROUP BY a.MATERIAL,a.MSIZE,a.HEIGHT,a.UNIT

SELECT a.*,b.name as material_name,c.name as workshop_name,
SUM(CASE WHEN a.state=0 THEN a.number ELSE 0 END)  as sum_in, 
SUM(CASE WHEN a.state=1 THEN a.number ELSE 0 END)  as sum_out 
FROM stock a 
left join material b on a.material=b.id 
left join workshop c on a.workshop=c.id 

	 AND a.ID =#{id} 
	 AND a.MATERIAL =#{material} 
	 AND a.STOCKTYPE =#{stocktype} 
	 AND a.WORKSHOP =#{workshop} 
	 AND a.UNIT =#{unit} 
	 AND a.MSIZE =#{msize} 
	 AND a.HEIGHT =#{height} 
	 AND a.NUMBER =#{number} 
	 AND a.COMMENT =#{comment} 
	 AND a.QUALIFY =#{qualify} 
	 AND a.DAMAGE =#{damage} 
	 AND a.STATE =#{state} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.PROCESS =#{process} 
	 AND a.PACKAGING =#{packaging} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.ORDERCODE =#{ordercode} 
	 AND a.OUTTYPE =#{outtype} 
	 AND a.MAOCHI =#{maochi} 
	 AND a.JINGCHI =#{jingchi} 
	 AND a.YANMI =#{yanmi} 
	 AND a.CODE =#{code} 
	 AND a.M_DT =#{m_dt} 
	 AND a.AUDITOR =#{auditor} 
	 AND a.WORKER =#{worker} 

GROUP BY a.MATERIAL,a.MSIZE,a.HEIGHT,a.UNIT

SELECT a.*,b.name as material_name,c.name as workshop_name,
SUM(CASE WHEN a.state=2 THEN substring_index(a.MSIZE,'*',1)*substring_index(a.MSIZE,'*',-1)*a.number/1000000 ELSE 0 END)  as sum_in, 
SUM(CASE WHEN a.state=1 THEN substring_index(a.MSIZE,'*',1)*substring_index(a.MSIZE,'*',-1)*a.number/1000000 ELSE 0 END)  as sum_out ,
SUM(CASE WHEN a.damage='是' THEN a.number ELSE 0 END) as damage_num,
SUM(CASE WHEN a.process='计划外入库' THEN a.number ELSE 0 END) as outside_sum
FROM stock a 
left join material b on a.material=b.id 
left join workshop c on a.workshop=c.id 

	 AND a.ID =#{id} 
	 AND a.MATERIAL =#{material} 
	 AND a.STOCKTYPE =#{stocktype} 
	 AND a.WORKSHOP =#{workshop} 
	 AND a.UNIT =#{unit} 
	 AND a.MSIZE =#{msize} 
	 AND a.HEIGHT =#{height} 
	 AND a.NUMBER =#{number} 
	 AND a.COMMENT =#{comment} 
	 AND a.QUALIFY =#{qualify} 
	 AND a.DAMAGE =#{damage} 
	 AND a.STATE =#{state} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.PROCESS =#{process} 
	 AND a.PACKAGING =#{packaging} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.ORDERCODE =#{ordercode} 
	 AND a.OUTTYPE =#{outtype} 
	 AND a.MAOCHI =#{maochi} 
	 AND a.JINGCHI =#{jingchi} 
	 AND a.YANMI =#{yanmi} 
	 AND a.CODE =#{code} 
	 AND unix_timestamp(a.M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(a.M_DT) <=unix_timestamp(#{m_dtTo}) 

	 AND a.M_DT =#{m_dt} 
	 AND a.AUDITOR =#{auditor} 
	 AND a.WORKER =#{worker} 

GROUP BY a.outtype,a.UNIT

SELECT a.*,b.name as material_name,c.name as workshop_name,
SUM(CASE WHEN a.qualify='是' THEN substring_index(a.MSIZE,'*',1)*substring_index(a.MSIZE,'*',-1)*a.number/1000000 ELSE 0 END)  as sum_in, 
SUM(CASE WHEN a.qualify='否' THEN substring_index(a.MSIZE,'*',1)*substring_index(a.MSIZE,'*',-1)*a.number/1000000 ELSE 0 END)  as sum_out ,
SUM(CASE WHEN a.outtype='计划外入库' THEN a.number ELSE 0 END)  as sum_number 
FROM stock a 
left join material b on a.material=b.id 
left join workshop c on a.workshop=c.id 

	 AND a.ID =#{id} 
	 AND a.MATERIAL =#{material} 
	 AND a.STOCKTYPE =#{stocktype} 
	 AND a.WORKSHOP =#{workshop} 
	 AND a.UNIT =#{unit} 
	 AND a.MSIZE =#{msize} 
	 AND a.HEIGHT =#{height} 
	 AND a.NUMBER =#{number} 
	 AND a.COMMENT =#{comment} 
	 AND a.QUALIFY =#{qualify} 
	 AND a.DAMAGE =#{damage} 
	 AND a.STATE =#{state} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.C_ID =#{c_id} 
	 AND a.PID =#{pid} 
	 AND a.PROCESS =#{process} 
	 AND a.PACKAGING =#{packaging} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.ORDERCODE =#{ordercode} 
	 AND a.OUTTYPE =#{outtype} 
	 AND a.MAOCHI =#{maochi} 
	 AND a.JINGCHI =#{jingchi} 
	 AND a.YANMI =#{yanmi} 
	 AND a.CODE =#{code} 
	 AND a.M_DT =#{m_dt} 
	 AND unix_timestamp(a.M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(a.M_DT) <=unix_timestamp(#{m_dtTo}) 

	 AND a.AUDITOR =#{auditor} 
	 AND a.WORKER =#{worker} 

GROUP BY a.OUTTYPE,a.HEIGHT,a.UNIT

