mapper.select:
SELECT *
FROM stoneblock

	 AND ID =#{id} 
	 AND CODE =#{code} 
	 AND unix_timestamp(S_DT) >=unix_timestamp(#{s_dtFrom}) 
	 AND unix_timestamp(S_DT) <=unix_timestamp(#{s_dtTo}) 
	 AND S_DT =#{s_dt} 
	 AND SOURCE =#{source} 
	 AND PLACE =#{place} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND NUMBER =#{number} 
	 AND LENGTH =#{length} 
	 AND WIDTH =#{width} 
	 AND HEIGHT =#{height} 
	 AND CUBE =#{cube} 
	 AND PRICE =#{price} 
	 AND SUM =#{sum} 
	 AND PLATENUMBER =#{platenumber} 
	 AND ACCOUNTDIFF =#{accountdiff} 
	 AND AUDITOR =#{auditor} 
	 AND EDITOR =#{editor} 
	 AND COMMENT =#{comment} 
	 AND STATE =#{state} 
	 AND BLOCKNUMBER =#{blocknumber} 
	 AND C_ID =#{c_id} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM stoneblock 

	 AND ID =#{id} 
	 AND CODE =#{code} 
	 AND S_DT =#{s_dt} 
	 AND unix_timestamp(S_DT) >=unix_timestamp(#{s_dtFrom}) 
	 AND unix_timestamp(S_DT) <=unix_timestamp(#{s_dtTo}) 
	 AND S_DT =#{s_dt} 
	 AND SOURCE =#{source} 
	 AND PLACE =#{place} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND NUMBER =#{number} 
	 AND LENGTH =#{length} 
	 AND WIDTH =#{width} 
	 AND HEIGHT =#{height} 
	 AND CUBE =#{cube} 
	 AND PRICE =#{price} 
	 AND SUM =#{sum} 
	 AND PLATENUMBER =#{platenumber} 
	 AND ACCOUNTDIFF =#{accountdiff} 
	 AND AUDITOR =#{auditor} 
	 AND EDITOR =#{editor} 
	 AND COMMENT =#{comment} 
	 AND STATE =#{state} 
	 AND BLOCKNUMBER =#{blocknumber} 
	 AND C_ID =#{c_id}

mapper.update:
UPDATE stoneblock
ID=#{id}, CODE=#{code}, S_DT=#{s_dt}, SOURCE=#{source}, PLACE=#{place}, C_DT=#{c_dt}, U_DT=#{u_dt}, NUMBER=#{number}, LENGTH=#{length}, WIDTH=#{width}, HEIGHT=#{height}, CUBE=#{cube}, PRICE=#{price}, SUM=#{sum}, PLATENUMBER=#{platenumber}, ACCOUNTDIFF=#{accountdiff}, AUDITOR=#{auditor}, EDITOR=#{editor}, COMMENT=#{comment}, STATE=#{state}, BLOCKNUMBER=#{blocknumber}, C_ID=#{c_id},
WHERE ID=#{id}
