mapper.select:
SELECT *
FROM workshop

	 AND ID =#{id} 
	 AND NAME LIKE #{name} 
	 AND LEADER =#{leader} 
	 AND COMMENT =#{comment} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND C_ID =#{c_id} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM workshop 

	 AND ID =#{id} 
	 AND NAME  LIKE #{name} 
	 AND LEADER =#{leader} 
	 AND COMMENT =#{comment} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND C_ID =#{c_id}

mapper.update:
UPDATE workshop
ID=#{id}, NAME=#{name}, LEADER=#{leader}, COMMENT=#{comment}, STATE=#{state}, C_DT=#{c_dt}, U_DT=#{u_dt}, C_ID=#{c_id},
WHERE ID=#{id}
