mapper.select:
SELECT *
FROM matteboard

	 AND ID =#{id} 
	 AND SB_ID =#{sb_id} 
	 AND SB_CODE =#{sb_code} 
	 AND SB_SPEC =#{sb_spec} 
	 AND SB_CUBE =#{sb_cube} 

	 AND CODE =#{code} 
	 AND AUDITOR =#{auditor} 
	 AND unix_timestamp(M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(M_DT) <=unix_timestamp(#{m_dtTo}) 
	 AND M_DT =#{m_dt} 
	 AND WORKGROUP =#{workgroup} 
	 AND LAYER =#{layer} 
	 AND MSIZE =#{msize} 
	 AND HEIGHT =#{height} 
	 AND BLOCKNUMBER =#{blocknumber} 
	 AND SQUARE =#{square} 
	 AND BELOWGRADEBLOCK =#{belowgradeblock} 
	 AND BELOWGRADESQUARE =#{belowgradesquare} 
	 AND COMMENT =#{comment} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATE =#{state} 
	 AND C_ID =#{c_id} 
	 AND PRICE =#{price} 
	 AND SUM =#{sum} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT a.m_dt,a.height , 
SUM(a.blocknumber)  as sum_blocknumber,SUM(a.square)  as sum_square,
SUM(a.blocknumber-a.belowgradeblock)  as sum_gradeblock,SUM(a.square-a.belowgradesquare)  as sum_gradesquare,
SUM(a.belowgradeblock)  as sum_belowgradeblock,SUM(a.belowgradesquare)  as sum_belowgradesquare 
FROM matteboard a 


	 AND a.ID =#{id} 
	 AND a.SB_ID =#{sb_id} 
	 AND a.SB_CODE =#{sb_code} 
	 AND a.SB_SPEC =#{sb_spec} 
	 AND a.SB_CUBE =#{sb_cube}  
	 AND a.CODE =#{code} 
	 AND a.AUDITOR =#{auditor} 
	 AND unix_timestamp(a.M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(a.M_DT) <=unix_timestamp(#{m_dtTo}) 
	 AND a.M_DT =#{m_dt} 
	 AND a.WORKGROUP =#{workgroup} 
	 AND a.LAYER =#{layer} 
	 AND a.MSIZE =#{msize} 
	 AND a.HEIGHT =#{height} 
	 AND a.BLOCKNUMBER =#{blocknumber} 
	 AND a.SQUARE =#{square} 
	 AND a.BELOWGRADEBLOCK =#{belowgradeblock} 
	 AND a.BELOWGRADESQUARE =#{belowgradesquare} 
	 AND a.COMMENT =#{comment} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATE =#{state} 
	 AND a.C_ID =#{c_id} 
	 AND a.PRICE =#{price} 
	 AND a.SUM =#{sum} 

group by a.height ,a.m_dt

SELECT COUNT(id) 
FROM matteboard 

	 AND ID =#{id} 
	 AND SB_ID =#{sb_id} 
	 AND SB_CODE =#{sb_code} 
	 AND SB_SPEC =#{sb_spec} 
	 AND SB_CUBE =#{sb_cube} 
	 AND CODE =#{code} 
	 AND AUDITOR =#{auditor} 
	 AND unix_timestamp(M_DT) >=unix_timestamp(#{m_dtFrom}) 
	 AND unix_timestamp(M_DT) <=unix_timestamp(#{m_dtTo}) 
	 AND M_DT =#{m_dt} 
	 AND WORKGROUP =#{workgroup} 
	 AND LAYER =#{layer} 
	 AND MSIZE =#{msize} 
	 AND HEIGHT =#{height} 
	 AND BLOCKNUMBER =#{blocknumber} 
	 AND SQUARE =#{square} 
	 AND BELOWGRADEBLOCK =#{belowgradeblock} 
	 AND BELOWGRADESQUARE =#{belowgradesquare} 
	 AND COMMENT =#{comment} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATE =#{state} 
	 AND C_ID =#{c_id} 
	 AND PRICE =#{price} 
	 AND SUM =#{sum}

mapper.update:
UPDATE matteboard
ID=#{id}, SB_ID=#{sb_id}, SB_CODE=#{sb_code}, SB_SPEC=#{sb_spec}, SB_CUBE=#{sb_cube}, CODE=#{code}, AUDITOR=#{auditor}, M_DT=#{m_dt}, WORKGROUP=#{workgroup}, LAYER=#{layer}, MSIZE=#{msize}, HEIGHT=#{height}, BLOCKNUMBER=#{blocknumber}, SQUARE=#{square}, BELOWGRADEBLOCK=#{belowgradeblock}, BELOWGRADESQUARE=#{belowgradesquare}, COMMENT=#{comment}, C_DT=#{c_dt}, U_DT=#{u_dt}, STATE=#{state}, C_ID=#{c_id}, PRICE=#{price}, SUM=#{sum},
WHERE ID=#{id}
