mapper.select:
SELECT *
FROM stock

	 AND ID =#{id} 
	 AND MATERIAL =#{material} 
	 AND STOCKTYPE =#{stocktype} 
	 AND WORKSHOP =#{workshop} 
	 AND UNIT =#{unit} 
	 AND MSIZE =#{msize} 
	 AND HEIGHT =#{height} 
	 AND NUMBER =#{number} 
	 AND COMMENT =#{comment} 
	 AND DAMAGE =#{damage} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND C_ID =#{c_id} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM stock 

	 AND ID =#{id} 
	 AND MATERIAL =#{material} 
	 AND STOCKTYPE =#{stocktype} 
	 AND WORKSHOP =#{workshop} 
	 AND UNIT =#{unit} 
	 AND MSIZE =#{msize} 
	 AND HEIGHT =#{height} 
	 AND NUMBER =#{number} 
	 AND COMMENT =#{comment} 
	 AND DAMAGE =#{damage} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND C_ID =#{c_id}

mapper.update:
UPDATE stock
ID=#{id}, MATERIAL=#{material}, STOCKTYPE=#{stocktype}, WORKSHOP=#{workshop}, UNIT=#{unit}, MSIZE=#{msize}, HEIGHT=#{height}, NUMBER=#{number}, COMMENT=#{comment}, DAMAGE=#{damage}, STATE=#{state}, C_DT=#{c_dt}, U_DT=#{u_dt}, C_ID=#{c_id},
WHERE ID=#{id}
