mapper.select:
SELECT *
FROM user

	 AND ID =#{id} 
	 AND USERNAME =#{username} 
	 AND PASSWORD =#{password} 
	 AND NICKNAME =#{nickname} 
	 AND COMMENT =#{comment} 
	 AND C_ID =#{c_id} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM user 

	 AND ID =#{id} 
	 AND USERNAME =#{username} 
	 AND PASSWORD =#{password} 
	 AND NICKNAME =#{nickname} 
	 AND COMMENT =#{comment} 
	 AND C_ID =#{c_id} 
	 AND STATE =#{state} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt}

mapper.update:
UPDATE user
ID=#{id}, USERNAME=#{username}, PASSWORD=#{password}, NICKNAME=#{nickname}, COMMENT=#{comment}, C_ID=#{c_id}, STATE=#{state}, C_DT=#{c_dt}, U_DT=#{u_dt},
WHERE ID=#{id}
